mapper.sql:
SELECT a.RESORT_ID , a.RESORT_NM , COUNT(b.BOOKING_DT) as BOOKING_CNT , SUM(CASE WHEN c.HOLIDAY_YN = 'Y' THEN 1 ELSE 0 END) as HOLIDAY_CNT , MIN(b.INSERT_DT) as INSERT_DT FROM RESORT a JOIN BOOKING b ON (b.RESORT_ID = a.RESORT_ID) JOIN CALENDAR c ON (c.DT = b.BOOKING_DT)
WHERE a.RESORT_ID = #{resortId}
GROUP BY a.RESORT_ID, a.RESORT_NM

mapper.select:
SELECT b.RESORT_ID , b.RESORT_NM , a.ROOM_NO , a.ROOM_NM , a.ROOM_TYPE , a.ROOM_TYPE_NM , a.OCCUPANCY , a.PRICE , a.PEAK_PRICE FROM ROOM a JOIN RESORT b ON ( b.RESORT_ID = a.RESORT_ID ) WHERE a.RESORT_ID = #{resortId}
AND a.ROOM_NO = #{roomNo}
ORDER BY a.ROOM_NO

SELECT b.RESORT_ID , b.RESORT_NM , a.BOOKING_DT , a.ROOM_NO , a.ROOM_NM , c.ROOM_TYPE , c.ROOM_TYPE_NM , c.OCCUPANCY , c.SPACE , c.PRICE , c.PEAK_PRICE FROM BOOKING a JOIN RESORT b ON ( b.RESORT_ID = a.RESORT_ID ) JOIN CALENDAR cal ON ( cal.DT = a.BOOKING_DT ) LEFT JOIN ROOM c ON ( c.RESORT_ID = a.RESORT_ID AND c.ROOM_NO = a.ROOM_NO ) WHERE a.RESORT_ID = #{resortId}
AND cal.HOLIDAY_YN = 'Y'
AND a.BOOKING_DT = #{bookingDt}
ORDER BY a.BOOKING_DT, a.ROOM_NO
